import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import classnames from "classnames";
import Card from "@material-ui/core/Card";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Collapse from "@material-ui/core/Collapse";
import IconButton from "@material-ui/core/IconButton";
import red from "@material-ui/core/colors/red";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import InputAdornment from "@material-ui/core/InputAdornment";
import classNames from 'classnames';
import TextField from '@material-ui/core/TextField';

const styles = theme => ({
  card: {
    maxWidth: 400,
    marginRight:"2%"
  },
  media: {
    height: 0,
    paddingTop: "56.25%" // 16:9
  },
  actions: {
    display: "flex"
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest
    })
  },
  expandOpen: {
    transform: "rotate(180deg)"
  },
  avatar: {
    backgroundColor: red[500]
  }
});


class Channels extends React.Component {
  state = {
     expanded1: false,
     expanded2: false,
     expanded3: false,
     amount: 0 
    };

  handleExpandClick1 = () => {
    this.setState(state => ({ expanded1: !state.expanded1 }));
  };

  handleExpandClick2 = () => {
    this.setState(state => ({ expanded2: !state.expanded2 }));
  };

  handleExpandClick3 = () => {
    this.setState(state => ({ expanded3: !state.expanded3 }));
  };

  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };


  render() {
    const { classes } = this.props;

    return (
      <div style={{ textAlign: "left", marginTop: "2%" }}>
        <Card
          className={classes.card}
          style={{ display: "inline-block", width: "20%" }}
        >
          <CardMedia
            className={classes.media}
            image="data:image/jpeg;base64,"
            title="Paella dish"
          />
          <CardActions className={classes.actions} disableActionSpacing>
            <IconButton aria-label="Add to favorites">
              <FavoriteIcon />
            </IconButton>
            <IconButton
              className={classnames(classes.expand, {
                [classes.expandOpen]: this.state.expanded
              })}
              onClick={this.handleExpandClick1}
              aria-expanded={this.state.expanded}
              aria-label="Show more"
            >
              <ExpandMoreIcon />
            </IconButton>
          </CardActions>
          <Collapse in={this.state.expanded1} timeout="auto" unmountOnExit>
            <CardContent>
              <p style={{fontSize:'0.7em'}}>Specify the ammount display for specific OTA's from Basic Rates in (%)</p>
              <TextField
                id="standard-adornment-amount"
                className={classNames(classes.margin, classes.textField)}
                variant="standard"
                label="Percentage"
                value={this.state.amount}
                onChange={this.handleChange("amount")}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">%</InputAdornment>
                  )
                }}
              />
            </CardContent>
          </Collapse>
        </Card>
        <Card
          className={classes.card}
          style={{ display: "inline-block", width: "20%" }}
        >
          <CardMedia
            className={classes.media}
            image="data:image/png;base64,"
            title="Paella dish"
          />
          <CardActions className={classes.actions} disableActionSpacing>
            <IconButton aria-label="Add to favorites">
              <FavoriteIcon />
            </IconButton>
            <IconButton
              className={classnames(classes.expand, {
                [classes.expandOpen]: this.state.expanded
              })}
              onClick={this.handleExpandClick2}
              aria-expanded={this.state.expanded}
              aria-label="Show more"
            >
              <ExpandMoreIcon />
            </IconButton>
          </CardActions>
          <Collapse in={this.state.expanded2} timeout="auto" unmountOnExit>
            <CardContent>
              <p style={{fontSize:'0.7em'}}>Specify the ammount display for specific OTA's from Basic Rates in (%)</p>
              <TextField
                id="standard-adornment-amount"
                className={classNames(classes.margin, classes.textField)}
                variant="standard"
                label="Percentage"
                value={this.state.amount}
                onChange={this.handleChange("amount")}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">%</InputAdornment>
                  )
                }}
              />
            </CardContent>
          </Collapse>
        </Card>
        <Card
          className={classes.card}
          style={{ display: "inline-block", width: "20%" }}
        >
          <CardMedia
            className={classes.media}
            image="data:image/png;base64,"
            title="Paella dish"
          />
          <CardActions className={classes.actions} disableActionSpacing>
            <IconButton aria-label="Add to favorites">
              <FavoriteIcon />
            </IconButton>
            <IconButton
              className={classnames(classes.expand, {
                [classes.expandOpen]: this.state.expanded
              })}
              onClick={this.handleExpandClick3}
              aria-expanded={this.state.expanded}
              aria-label="Show more"
            >
              <ExpandMoreIcon />
            </IconButton>
          </CardActions>
          <Collapse in={this.state.expanded3} timeout="auto" unmountOnExit>
            <CardContent>
              <p style={{fontSize:'0.7em'}}>Specify the ammount display for specific OTA's from Basic Rates in (%)</p>
              <TextField
                id="standard-adornment-amount"
                className={classNames(classes.margin, classes.textField)}
                variant="standard"
                label="Percentage"
                value={this.state.amount}
                onChange={this.handleChange("amount")}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">%</InputAdornment>
                  )
                }}
              />
            </CardContent>
          </Collapse>
        </Card>
      </div>
    );
  }
}

Channels.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(Channels);
